const state = {
  userInfo: null
}

const mutations = {
  set(state, userInfo) {
    state.userInfo = userInfo
  }
}

const actions = {
  setUserInfo(){
        // 异步获取用户信息的逻辑
    // 调用commit方法来提交mutation
    commit('SET_USER_INFO', userInfo)
  }
}

export default {
  namespaced: true,
  state,
  mutations,
  actions
}
