const state = {
  //属性
  userInfo: null
}

const mutations = {
  //改变
  SET_USER_INFO(state, userInfo) {
    state.userInfo = userInfo
  }
}

const actions = {
  //事件
  actionUserInfo(){
    console.log("ccsh");
    // 调用commit方法来提交mutation
    commit('SET_USER_INFO', '123')
  }
}

export default {
  namespaced: true,
  state,
  mutations,
  actions
}
