const state = {
  userInfo: null
}

const mutations = {
  SET_USER_INFO(state, userInfo) {
    state.userInfo = userInfo
  }
}

const actions = {
  setUserInfo
}

export default {
  namespaced: true,
  state,
  mutations,
  actions
}
