const state = {
  //属性
  data: null
}

const mutations = {
  //改变
  hanldeUserInfo(state, val) {
    state.data = val
  }
}

const actions = {
  //事件
  actionUserInfo({ commit }){
    // 调用commit方法来提交mutation
    commit('hanldeData', {"name":'ccsh'})
  }
}

export default {
  namespaced: true,
  state,
  mutations,
  actions
}
